            },
            body: JSON.stringify(userData),
            mode: "cors"
        })
      }

      const handleChange = (e) => {
        setUserData({...userData, [e.target.name]: e.target.value})
    }



    return (

        <div className="div">

            <div id="darkened">


            <h1>Create a new post</h1>
            <h2>-------------------------------------------------</h2>


            <form onSubmit={submit} onChange={handleChange}> 



