import * as React from 'react';
import {useState, useEffect, useRef} from 'react'

import {useParams} from 'react-router-dom'
import { useNavigate } from "react-router-dom";


function DeletePost() {



    const navigate = useNavigate();


    window.scroll(0,0)

    const [data, setData] = useState({})

    const {id} = useParams()

    const [dataType, setDataType] = useState("post")



    function fetchPost () {

        useEffect(() => {

            window.scroll(0,0)



            fetch("/api/post/"+id)
            .then(response => response.json())
            .then(json => {



                setData(json)






            })








        }, [dataType])



    }








    const submitYes = (event) => {
        event.preventDefault();



        var token= window.localStorage.getItem("token")



        fetch("/api/token/check", {
            method: "POST",

            headers: {
              "authorization": token
            },

            body: null,


          }).then(response => {

            return response.json()}).then(json => {

          if (json.message== "VERIFIED") {





            var username= json.username

            var id= json._id;

            var subject= String(event.target[0].value);

            var description= String(event.target[1].value);





            var code= String(event.target[2].value);




            fetch("/api/deletesnippet", {
                method: "POST",

                body: JSON.stringify({
                    postID: data.id,
                    subject: subject,
                    author: data.author,
                    description: description,

                    code: code,

                }),



            }).then(response => {

                return response.json()})
            .then(json => {

                if (!json.success) {

                    if (json.msg= "Password incorrect") {
                        alert("Wrong password")
                    }

                    else if (json.msg= "User not found!") {
                        alert("Invalid credentials!")

                    }
                    else {
                        alert("Something else is wrong, error")
                    }
                }

                else {

                    navigate("/")

                    window.location.reload();
                }
            });






          }

          else {
            navigate("/random")
          }
        });






}

