import Comment from "./Comment"

const CommentList = ({postComments}) => {
    return (
        <div id="list">

            {postComments.slice(0).reverse().map((post) => (
            <Comment  key={post.id} post={post} />

            ))}
        </div>
    )
}

export default CommentList
