import * as React from 'react';
import { Button } from '@mui/material'
import {useState, useEffect, useRef} from 'react'
import {Link} from 'react-router-dom'
import {useParams} from 'react-router-dom'
import "highlight.js/styles/github.css";
import SyntaxHighlighter from 'react-syntax-highlighter';
import { irBlack } from 'react-syntax-highlighter/dist/esm/styles/hljs';
import CommentList from './CommentList'
import NewComment from './NewComment'





const Post = () => {



    const [auth, setAuth] = React.useState(false);
    const [user, setUser] = React.useState("");
    const [height, setHeight] = useState(0);
    const appRef = useRef(null);



    const CheckLogin = () => {

        var token= window.localStorage.getItem("token")

        var username= window.localStorage.getItem("username")

        fetch("/api/token/check", {
          method: "POST",

          headers: {
            "authorization": token
          },

          body: null,


        }).then(response => {

          return response.json()})
      .then(json => {

        if (json.message== "VERIFIED") {

          setAuth(true);

          setUser(username);



        }

        else {
          setAuth(false);
        }



      })
      };






    const [data, setData] = useState({})

    const {id} = useParams()

    const [dataType, setDataType] = useState("post")


    useEffect(() => {

        window.scroll(0,0)



        fetch("/api/post/"+id)
        .then(response => response.json())
        .then(json => {



            setData(json)




        })


        const appHeight = appRef.current.scrollHeight;
        setHeight(appHeight);







    }, [dataType])


    CheckLogin();



    return (

    <div className="div">
        <div id="darkened" ref={appRef} >
            <div id="head"></div>

            <h1>{data.subject}</h1>







              <h2>-------------------------------------------------</h2>

              <div id="notelabel" >

              <h6>{data.description}</h6>

              </div>




                <div id="post">

                    <div id= "author">
                        <a id="created" >Created by:</a>
                        <Button component={Link} to={"/profile/"+data.author} id="created"> {data.author}</Button>

                        {user=data.author && <Button component={Link} to={"/profile/"+data.author} id="created"> Edit this post</Button>}




                        <a id= "whenPost">Last edited: {data.date}</a>
                        <p></p>
                    </div>





                    <div >



                        <SyntaxHighlighter id= "code" language={data.codelang} style={irBlack} > 
                            {data.code}

                        </SyntaxHighlighter>


                    </div>

                    <div>

                        <CommentList />










                    </div>




                </div>

                <div id="NewComment">

                {auth &&<NewComment data= {data}/>}

                {!auth &&<div id="NewComment"><h6>Log in or register to write a comment!</h6></div>}





                </div>

            </div>       

         </div>





    )



}

export default Post
